mapper.select:
SELECT user_id, user_email, user_pw, user_name, role, refresh_token, alias, phone_num
FROM user_table
WHERE user_id = #{userId}

SELECT user_id, user_email, user_pw, user_name, role, refresh_token, alias, phone_num
FROM user_table
WHERE user_email = #{userEmail}

SELECT user_id, user_email, user_pw, user_name, role, refresh_token, alias, phone_num
FROM user_table
WHERE user_email = #{userEmail} AND user_pw = #{userPw}

SELECT user_id, user_email, user_pw, user_name, role, refresh_token, alias, phone_num
FROM user_table
WHERE refresh_token = #{refreshToken}

SELECT user_id, user_email, user_pw, user_name, role, refresh_token, alias, phone_num
FROM user_table
WHERE role = #{role}

SELECT dib_id, user_id, movie_id
FROM dib_table
WHERE user_id = #{userId}
